const DashboardLayout = ({ children, activePage = "/dashboard" }: DashboardLayoutProps) => {
  const [sidebarOpen, setSidebarOpen] = useState(false);

  const navItems = [
    { icon: dashboardIcon, label: "Dashboard", href: "/dashboard" },
    { icon: listingIcon, label: "Listing", href: "/listings" },
    { icon: OrdersIcon, label: "Orders", href: "/orders" },
    { icon: PromotionIcon, label: "Promotions", href: "/promotions" },
    { icon: SettingsIcon, label: "Settings", href: "/settings" },
    { icon: SecurityIcon, label: "Security", href: "/security" },
  ];


  return (
    <div className="min-h-screen bg-[#f9f9f9] flex">
      {/* Mobile Sidebar Overlay */}
      {sidebarOpen && (
        <div
          className="fixed inset-0 z-40 bg-gray-600 opacity-65 lg:hidden"
          onClick={() => setSidebarOpen(false)}
        >
          <button
            onClick={() => setSidebarOpen(false)}
            className="lg:hidden p-1 hover:bg-gray-100 rounded absolute top-3 right-3"
          >
            <MdClose size={26} className="text-white" />
          </button>
        </div>
      )}

      {/* Sidebar */}
      <div
        className={`
        fixed inset-y-0 left-0 top-0 max-h-screen z-50 xl:w-72 xl:min-w-72 w-64  transform bg-[#2d3032]  
        transition-transform duration-300 ease-in-out lg:translate-x-0 lg:sticky overflow-auto [scrollbar-width:none] [-ms-overflow-style:none] [&::-webkit-scrollbar]:hidden
        ${sidebarOpen ? "translate-x-0" : "-translate-x-full"}
      `}
      >
        {/* Logo */}
        <div className="flex items-center justify-between lg:pt-8 pt-4 px-4">
          <div className="flex items-center">
            <img src="/sidebarLogo.png" alt="" />
          </div>
          
        </div>

        {/* Navigation */}
        <nav className="flex-1 py-6 space-y-3.5 mt-8">
          {navItems.map((item) => (
            <button
              key={item.label}
              className={`
                w-full xl:px-6 px-4 py-3 text-sm font-medium rounded-e-lg transition-all duration-200
                ${
                  (activePage === item.href)
                    ? "bg-[#FFB300] text-[#272727] "
                    : "text-white hover:bg-gray-100 hover:text-gray-900"
                }
              `}
            >
              <Link href={item.href} className="flex items-center ">
                <span className="mr-3">
                  <item.icon />
                </span>
                {item.label}
              </Link>
            </button>
          ))}
        </nav>
      </div>

      {/* Main Content */}
      <div className="w-full">
        {/* Top Header */}
        <header className="sticky top-0 z-50 flex py-5 items-center justify-between px-4 sm:px-6 lg:px-8 bg-white shadow-sm">
          <button
            onClick={() => setSidebarOpen(true)}
            className=" p-2 text-gray-700 hover:bg-gray-100 rounded-lg lg:hidden"
          >
            <MdMenu size={24} />
          </button>

          {/* Search */}
          <div className="relative flex-1 max-w-107">
            <MdSearch className="absolute right-3 top-1/2 h-5 w-5 -translate-y-1/2 text-gray-400" />
            <input
              type="search"
              className="w-full pl-3 pr-10 py-2 border border-[#E3E3E3] rounded-md 
                         text-base placeholder-[#555555] focus:outline-none focus:ring-1 bg-white
                         focus:ring-[#FFB300] focus:border-transparent"
              placeholder="Search..."
            />
          </div>

          {/* Notifications & User */}
          <div className="flex items-center md:gap-5 gap-3 lg:ml-0 ml-4">
            <button className="relative bg-[#F7F7F7] w-10 h-10 flex items-center justify-center text-gray-600 hover:text-gray-900 hover:bg-gray-100 rounded-lg">
              <AiOutlineMessage size={24} />
            </button>

            <button className="relative bg-[#F7F7F7] w-10 h-10 flex items-center justify-center text-gray-600 hover:text-gray-900 hover:bg-gray-100 rounded-lg">
              <div className="relative">
              <MdNotificationsNone size={24} />
              <span className="absolute top-1 right-1.25 h-1.5 w-1.5 rounded-full bg-red-500 border-gray-200" />
              </div>
            </button>

            {/* <div className="h-6 w-px bg-gray-200 hidden lg:block" /> */}

            <div className="hidden lg:flex items-center gap-5">
              <div className="h-15 w-15 rounded-md flex items-center justify-center">
                <img
                  src="/userImg.png"
                  className="w-full h-full object-cover rounded-md"
                />
              </div>
              <div>
                <DropdownMenu>
                  <DropdownMenuTrigger asChild>
                    <div className="flex items-center gap-2 cursor-pointer">
                    <p className="text-base font-medium ">John Parker</p>
                    <IoIosArrowDown/>
                    </div>
                  </DropdownMenuTrigger>
                  <DropdownMenuContent className="w-56" align="start">
                    <DropdownMenuLabel>My Account</DropdownMenuLabel>
                    <DropdownMenuItem> Profile </DropdownMenuItem>
                    <DropdownMenuItem> Help </DropdownMenuItem>
                    <DropdownMenuItem> Log out </DropdownMenuItem>
                  </DropdownMenuContent>
                </DropdownMenu>
                <p className="text-sm text-gray-500">Branch Admin</p>
              </div>
            </div>
          </div>
        </header>

        {/* Page Content */}
        <main className="py-5 px-4 sm:px-6 lg:px-8">{children}</main>
      </div>
    </div>
  );
};

export default DashboardLayout;
